import React from 'react';
import { View, StyleSheet, Image } from 'react-native';
import { Divider, Toolbar, AppBar } from 'react95-native';

import { TouchableOpacity } from 'react-native-gesture-handler';
import { notificationService } from '../util/notifications';

const icons = [
  {
    label: 'Notepad',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'PowerOff',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Tree',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Unmute',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Keys',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Defrag',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'MediaCD',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Bookmark',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Brush',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Earth',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'Dial',
    uri:
      'data:image/png;base64,',
  },
  {
    label: 'TimeDate',
    uri:
      'data:image/png;base64,',
  },
];

const ToolbarExample = () => {
  return (
    <View style={{ backgroundColor: 'teal', flex: 1 }}>
      <AppBar style={{ padding: 2 }}>
        <AppBar.BackAction onPress={() => {}} />
        <AppBar.Content title='Office' subtitle='tools' />
        <View style={styles.wrapper}>
          <Divider orientation='vertical' style={{ marginRight: 4 }} />
          <Divider
            variant='raised'
            orientation='vertical'
            style={{ height: '80%' }}
          />
          <View style={styles.toolbarWrapper}>
            <Toolbar>
              {icons.map(icon => (
                <TouchableOpacity
                  key={icon.label}
                  onPress={() =>
                    notificationService.send({
                      message: `You just pressed ${icon.label} icon!`,
                      closeButtonLabel: 'OK!',
                    })
                  }
                >
                  <Image
                    source={{ uri: icon.uri }}
                    style={styles.item}
                    key={icon.label}
                  />
                </TouchableOpacity>
              ))}
            </Toolbar>
          </View>
        </View>
      </AppBar>
    </View>
  );
};

const styles = StyleSheet.create({
  wrapper: {
    flexDirection: 'row',
    alignItems: 'center',
    height: 40,
    minWidth: 200,
  },
  toolbarWrapper: {
    flex: 1,
    paddingHorizontal: 4,
  },
  item: {
    marginHorizontal: 8,
    width: 32,
    height: 32,
  },
});

export default ToolbarExample;
