import React, { useState } from 'react';
import { StyleSheet, View, Image, Linking } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import {
  Panel,
  AppBar,
  Button,
  List,
  Text,
  ScrollView,
  ScrollPanel,
  Divider,
  Window,
  Anchor,
  useTheme,
} from 'react95-native';
import type { Theme } from 'react95-native';
import examples from './examples';
import themes from './examples/themes';
import ThemeButton from './util/ThemeButton';
import GenericLogo from './assets/images/gcp.png';

type Props = {
  setTheme: (theme: Theme) => void;
};

const ExamplesScreen = ({ setTheme: setThemeProp }: Props) => {
  const [showAboutModal, setShowAboutModal] = useState(false);
  const navigation = useNavigation();
  // const { theme: currentTheme, setTheme } = useContext(LocalThemeContext);

  const currentTheme = useTheme();
  const openLink = (url: string) => {
    Linking.openURL(url).catch(err => console.warn("Couldn't load page", err));
  };

  return (
    <View style={styles.container}>
      {showAboutModal ? (
        <Window
          title='About'
          style={{ flex: 1 }}
          onClose={() => setShowAboutModal(false)}
        >
          <View
            style={{
              padding: 16,
              justifyContent: 'space-between',
              flex: 1,
            }}
          >
            <Panel variant='cutout' background='material' style={styles.cutout}>
              <ScrollView
                style={styles.scrollView}
                scrollViewProps={{
                  contentContainerStyle: styles.content,
                }}
              >
                <View>
                  <Text
                    bold
                    style={{
                      fontSize: 22,
                      marginBottom: 16,
                    }}
                  >
                    About Generic Coin
                  </Text>

                  <Text style={{ lineHeight: 24 }}>
                    This <Text style={{ color: 'red' }}>PRERELEASE</Text>{' '}
                    version of React95 Native is under construction!
                    {'\n'}
                    {'\n'}
                    Please remember that the components, themes and API will
                    continue to change through the product&apos;s development
                    cycle.
                    {'\n'}
                    {'\n'}
                    If you find this project interesting consider doing the
                    following:
                    {'\n'}
                    {'\n'}- follow our{' '}
                    <Anchor
                      underline
                      onPress={() => openLink('https://twitter.com/react95_io')}
                    >
                      Twitter account
                    </Anchor>
                    {'\n'}- visit our{' '}
                    <Anchor
                      underline
                      onPress={() => openLink('https://react95.io/')}
                    >
                      Website
                    </Anchor>
                    {'\n'}- sponsor us on{' '}
                    <Anchor
                      underline
                      onPress={() =>
                        openLink('https://www.patreon.com/arturbien')
                      }
                    >
                      Patreon
                    </Anchor>
                    {'\n'}- donate through{' '}
                    <Anchor
                      underline
                      onPress={() =>
                        openLink('https://www.paypal.com/paypalme/react95')
                      }
                    >
                      PayPal
                    </Anchor>
                    {'\n'}- tell your friends!
                    {'\n'}
                    {'\n'}
                    Thanks!
                    {'\n'}
                    {'\n'}- the React95 team
                  </Text>
                </View>
              </ScrollView>
            </Panel>
            <View>
              <Divider style={{ marginTop: 16 }} />
              <Button
                primary
                style={{ marginTop: 16, alignSelf: 'flex-end', width: 150 }}
                onPress={() => setShowAboutModal(false)}
              >
                OK
              </Button>
            </View>
          </View>
        </Window>
      ) : (
        <>
          <AppBar style={styles.header}>
            <View style={styles.logo}>
              <Image
                style={styles.logoImage}
                source={GenericLogo}
              />
              <Text style={styles.heading} bold disabled>
                Generic Coin
              </Text>
            </View>
            <Button
              square
              variant='raised'
              size='lg'
              style={styles.aboutButton}
              onPress={() => setShowAboutModal(true)}
            >
              <Image
                style={styles.questionMark}
                source={{
                  uri:
                    'data:image/png;base64,',
                }}
              />
            </Button>
          </AppBar>
          {/* <ScrollPanel style={styles.scrollPanel}>
            {themes.map(theme => (
              <ThemeButton
                theme={theme}
                currentTheme={currentTheme}
                selected={theme.name === currentTheme.name}
                onPress={() => setThemeProp(theme)}
                key={theme.name}
              />
            ))}
          </ScrollPanel> */}
          <Panel variant='raised' style={styles.panel}>
            <Divider />
            <Panel variant='cutout' background='canvas' style={styles.cutout}>
              <ScrollView
                style={styles.scrollView}
                scrollViewProps={{
                  contentContainerStyle: styles.content,
                }}
                alwaysShowScrollbars
              >
                <List.Accordion
                  title='Components'
                  style={styles.section}
                  defaultExpanded
                >
                  {examples.map((section, i) => (
                    <React.Fragment key={section.title}>
                      {i > 0 && <List.Divider />}
                      <List.Section title={section.title}>
                        {section.items.map(item => (
                          <List.Item
                            title={item.title}
                            key={item.title}
                            onPress={() => navigation.navigate(item.name)}
                          />
                        ))}
                      </List.Section>
                    </React.Fragment>
                  ))}
                </List.Accordion>
              </ScrollView>
            </Panel>
            <View style={[styles.statusBar]}>
              <Panel
                variant='well'
                style={[styles.statusBarItem, { flexGrow: 1, marginRight: 4 }]}
              >
                <Text>Current theme: {currentTheme.name}</Text>
              </Panel>
              <Panel variant='well' style={[styles.statusBarItem]}>
                <Text>        <a
                    href="mailto:genericcoin@outlook.com"
                    target="_blank"
                    rel="noreferrer"
                  >
                    genericcoin@outlook.com
                  </a>
                </Text>
              </Panel>
            </View>
          </Panel>
        </>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
  listItem: {
    height: 40,
    paddingHorizontal: 18,
  },
  panel: {
    flex: 1,
    padding: 8,
    marginTop: -4,
    paddingTop: 12,
  },
  cutout: {
    flexGrow: 1,
    marginTop: 8,
  },
  content: {
    padding: 16,
  },
  scrollView: {
    flex: 1,
  },
  section: {
    marginBottom: 16,
  },
  statusBar: {
    display: 'flex',
    flexDirection: 'row',
    width: '100%',

    marginTop: 4,
  },
  statusBarItem: {
    paddingHorizontal: 6,
    height: 32,
    justifyContent: 'center',
  },
  header: {
    justifyContent: 'center',
    marginBottom: -4,
    zIndex: 10,
  },
  logo: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'center',
    top: 2,
  },
  logoImage: {
    position: 'absolute',
    left: -38,
    top: -4,
    height: 32,
    width: 32,
    resizeMode: 'cover',
  },
  heading: {
    fontSize: 24,
    fontStyle: 'italic',
  },
  aboutButton: {
    position: 'absolute',
    right: 8,
    height: 40,
    width: 40,
  },
  questionMark: {
    width: 26,
    height: 26,
  },
  scrollPanel: {
    zIndex: -1,
  },
});

export default ExamplesScreen;
